SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_exame_mat_especial ( cd_exame_p text, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
nr_seq_exame_w		bigint;


BEGIN

if (cd_exame_p IS NOT NULL AND cd_exame_p::text <> '') or (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '')then


	if (coalesce(nr_seq_exame_p::text, '') = '') then

		select	nr_seq_exame
		into STRICT	nr_seq_exame_w
		from	exame_laboratorio
		where	cd_exame = cd_exame_p;

	else
		nr_seq_exame_w := nr_seq_exame_p;

	end if;


	select	max(ie_material_especial)
	into STRICT	ie_retorno_w
	from	material_exame_lab b,
		exame_lab_material a
	where	a.nr_seq_material 	= b.nr_sequencia
	and	a.nr_seq_exame 		= nr_seq_exame_w
	and	a.ie_situacao		= 'A'
	and	ie_prioridade		=
				(SELECT	min(ie_prioridade)
				from	exame_lab_material x
				where	nr_seq_exame	= nr_seq_exame_w
				and	x.ie_situacao	= 'A');


end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
