CREATE OR REPLACE FUNCTION obter_se_exame_patologia ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_exame_w		bigint;
ie_anatomia_w		varchar(1);


BEGIN

select	max(nr_seq_exame)
into STRICT	nr_seq_exame_w
from	prescr_procedimento
where	nr_prescricao = nr_prescricao_p
and	nr_sequencia = nr_sequencia_p;

if (nr_Seq_exame_w IS NOT NULL AND nr_Seq_exame_w::text <> '') then

	select	coalesce(ie_anatomia_patologica,'N')
	into STRICT	ie_anatomia_w
	from	exame_laboratorio
	where	nr_seq_exame = nr_seq_exame_w;

else

	ie_anatomia_w	:= 'N';

end if;

return	ie_anatomia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
