CREATE OR REPLACE FUNCTION obter_se_exame_protocolo_pep ( nr_seq_protocolo_p bigint, nr_seq_exame_p bigint, nr_seq_material_p bigint, nr_seq_exame_prescr_p bigint default null) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(1);


BEGIN

ds_retorno_w	:= 'S';

if (nr_seq_protocolo_p > 0) then
	ds_retorno_w	:= 'N';

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from   	pep_exame_grid_item
	where	nr_seq_pep_exame_grid = nr_seq_protocolo_p
	and	nr_seq_exame = nr_Seq_exame_p;

	if (ds_retorno_w = 'N') then
		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from   	pep_exame_grid_item a,
			exame_laboratorio b
		where	a.nr_seq_grupo = b.nr_seq_grupo
		and	a.nr_seq_pep_exame_grid = nr_seq_protocolo_p
		and	b.nr_seq_exame = nr_Seq_exame_p
		and coalesce(a.nr_seq_exame::text, '') = '';
	end if;

	if (ds_retorno_w = 'N') then
		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from   	pep_exame_grid_item
		where	nr_seq_pep_exame_grid = nr_seq_protocolo_p
		and	nr_seq_material = nr_seq_material_p
		and coalesce(nr_seq_exame::text, '') = '';
	end if;

	if (nr_seq_exame_prescr_p IS NOT NULL AND nr_seq_exame_prescr_p::text <> '') and (ds_retorno_w = 'N') then
		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from   	pep_exame_grid_item
		where	nr_seq_pep_exame_grid = nr_seq_protocolo_p
		and	nr_seq_exame = nr_seq_exame_prescr_p
		and	IE_APLICA_ANALITO	= 'S';

	end if;


end if;

return	ds_retorno_w;

end;
$body$
