CREATE OR REPLACE FUNCTION obter_se_exame_realizado_dia (nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_nao_realizado_w		varchar(1);
dt_prevista_w		timestamp;



BEGIN

select	dt_prev_execucao
into STRICT	dt_prevista_w
from	prescr_procedimento
where	nr_prescricao = nr_prescricao_p
and		nr_sequencia  = nr_seq_prescr_p;

if (to_char(clock_timestamp(),'dd/mm/yyyy') = to_char(dt_prevista_w,'dd/mm/yyyy')) then
	ie_nao_realizado_w := 'N';
else
	ie_nao_realizado_w := 'S';
end if;

return	ie_nao_realizado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
