CREATE OR REPLACE FUNCTION obter_se_exame_regra_info ( nr_seq_exame_p exame_lab_result_item.nr_seq_exame%type, nr_atendimento_p prescr_medica.nr_atendimento%type, cd_pessoa_fisica_p prescr_medica.cd_pessoa_fisica%type, ie_atendimento_p text, qt_minutos_p bigint) RETURNS bigint AS $body$
DECLARE


dt_minutos_w	timestamp;
qt_retorno_w	bigint;
						
c01 CURSOR FOR
	SELECT
		m.nr_seq_resultado,
		m.dt_resultado
	from	prescr_medica k,
			prescr_procedimento l,
			exame_lab_resultado m
	where	k.nr_atendimento	= nr_atendimento_p
	and 	l.nr_prescricao		= k.nr_prescricao
	and 	m.nr_prescricao		= k.nr_prescricao;
	
c02 CURSOR FOR
	SELECT
		m.nr_seq_resultado,
		m.dt_resultado
	from	prescr_medica k,
			prescr_procedimento l,
			exame_lab_resultado m
	where	k.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and 	l.nr_prescricao		= k.nr_prescricao
	and 	m.nr_prescricao		= k.nr_prescricao;
BEGIN

qt_retorno_w  := 0;

dt_minutos_w := clock_timestamp() - qt_minutos_p / 1440;

if  coalesce(ie_atendimento_p, 'S') = 'S' then

	for r_c01_w in c01
	loop
		select	count(n.nr_sequencia) + qt_retorno_w
		into STRICT	qt_retorno_w
		from	exame_lab_result_item n
		where	n.nr_seq_resultado	= r_c01_w.nr_seq_resultado
		and 	n.nr_seq_exame		= nr_seq_exame_p
		and 	((coalesce(qt_minutos_p, 0)  = 0) or (n.dt_aprovacao > dt_minutos_w) or (r_c01_w.dt_resultado > dt_minutos_w));
	end loop;

else

	for r_c02_w in c02
	loop
		select	count(n.nr_sequencia) + qt_retorno_w
		into STRICT	qt_retorno_w
		from	exame_lab_result_item n
		where	n.nr_seq_resultado	= r_c02_w.nr_seq_resultado
		and 	n.nr_seq_exame		= nr_seq_exame_p
		and 	((coalesce(qt_minutos_p, 0)  = 0) or (n.dt_aprovacao > dt_minutos_w) or (r_c02_w.dt_resultado > dt_minutos_w));
	end loop;

end if;

return  qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
