CREATE OR REPLACE FUNCTION obter_se_exame_regra_lanc ( nr_seq_exame_lanc_p bigint, nr_prescricao_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


contador_w				integer;
ds_retorno_w			varchar(1);


BEGIN

ds_retorno_w	:= 'N';

if 	(nr_seq_exame_lanc_p IS NOT NULL AND nr_seq_exame_lanc_p::text <> '' AND nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	count(*)
	into STRICT	contador_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and		nr_seq_exame in (SELECT	nr_seq_exame_prescr
							from	lab_alerta_lancamento
							where	nr_seq_exame_lanc = nr_seq_exame_lanc_p
							and		coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
							and		ie_situacao = 'A');

	if (contador_w > 0) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
