CREATE OR REPLACE FUNCTION obter_se_exame_visualizado_pa ( nr_seq_item_pront_p bigint, nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_status_atend_p bigint, ie_status_laudo_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(10)	:= 'N';
qt_reg_w	bigint	:= 0;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescricao_p IS NOT NULL AND nr_seq_prescricao_p::text <> '') then

	if (nr_seq_item_pront_p	= 2) then

		select	count(*)
		into STRICT	qt_reg_w
		from	prescr_proc_vis_pa
		where	nr_prescricao	  = nr_prescricao_p
		and	nr_seq_prescricao = nr_seq_prescricao_p
		and	ie_status_atend	  = ie_status_atend_p
		and	nm_usuario	= nm_usuario_p;

	elsif (nr_seq_item_pront_p	= 3) then

		select	count(*)
		into STRICT	qt_reg_w
		from	prescr_proc_vis_pa
		where	nr_prescricao	  = nr_prescricao_p
		and	nr_seq_prescricao = nr_seq_prescricao_p
		and	ie_status_laudo	  = ie_status_laudo_p
		and	nm_usuario	= nm_usuario_p;

	end if;

	if (qt_reg_w	> 0) then
		ie_retorno_w	:= 'S';

	end if;

end if;



return	ie_retorno_w;

end;
$body$
