CREATE OR REPLACE FUNCTION obter_se_exibe_acao ( ie_acao_p bigint, cd_material_p bigint, nr_seq_item_cme_p bigint, nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_w	varchar(1) := 'N';
qt_saldo_w	integer;


BEGIN

if (ie_acao_p in (1,5))  then
	ie_exibe_w	:= 'N';
elsif (ie_acao_p in (2,3,4)) then
	select	sum(CASE WHEN ie_acao=1 THEN qt_item  ELSE 0 END  - CASE WHEN ie_acao=2 THEN qt_item WHEN ie_acao=3 THEN qt_item WHEN ie_acao=4 THEN qt_item  ELSE 0 END )
	into STRICT	qt_saldo_w
	from	cirurgia_item_controle
	where	nr_cirurgia	=	nr_cirurgia_p
	and		((cd_material		=	cd_material_p) or (nr_seq_item_cme 	= nr_seq_item_cme_p))
	and	coalesce(ie_situacao,'A') = 'A';

	if (qt_saldo_w > 0) then
		ie_exibe_w	:= 'S';
	end if;
end if;

return	ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
