CREATE OR REPLACE FUNCTION obter_se_exibe_adds ( nr_seq_adds_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_w		varchar(10)	:= 'S';		
cd_setor_atendimento_w	bigint;
cd_perfil_w		bigint;
qt_reg_w		bigint;
	
			

BEGIN


cd_perfil_w			:= coalesce(obter_perfil_ativo,0);
cd_setor_atendimento_w		:= coalesce(obter_setor_atendimento(nr_atendimento_p),0);

select	count(1)
into STRICT	qt_reg_w
from	adds_regra
where	nr_seq_adds	= nr_seq_adds_p;

if (qt_reg_w	> 0) then

	select	count(1)
	into STRICT	qt_reg_w
	from	adds_regra
	where	nr_seq_adds				= nr_seq_adds_p
	and	coalesce(cd_perfil,cd_perfil_w)			= cd_perfil_w
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w;
	
	if (qt_reg_w	= 0) then
		ie_exibe_w	:= 'N';
	end if;
	

end if;


return	ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
