CREATE OR REPLACE FUNCTION obter_se_exibe_alerta ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, nm_usuario_p text, cd_funcao_ativa_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(10);
ie_exibe_alerta_w		varchar(1)	:= 'N';
dt_inicial_w			timestamp;
dt_final_w				timestamp;
cd_perfil_w				bigint	:= coalesce(obter_perfil_ativo,0);
VarDiasPeriodoAlerta	bigint;

					 

BEGIN 
 
cd_pessoa_fisica_w := obter_dados_usuario_opcao(nm_usuario_p,'C');
 
VarDiasPeriodoAlerta := obter_param_usuario(7032, 10, cd_perfil_w, nm_usuario_p, 0, VarDiasPeriodoAlerta);
VarDiasPeriodoAlerta := coalesce(VarDiasPeriodoAlerta,30);
 
dt_inicial_w	:= trunc(clock_timestamp() - VarDiasPeriodoAlerta);
dt_final_w		:= fim_dia(clock_timestamp());
 
if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_exibe_alerta_w 
	from 	ev_evento e, 
			ev_evento_pac_destino a, 
			ev_evento_paciente b, 
			ev_evento_regra_dest d, 
			ev_regra_dest_tela c where		e.nr_sequencia 	= b.nr_seq_evento 
	and 	b.nr_sequencia 	= a.nr_seq_ev_pac 
	and 	e.nr_sequencia 	= d.nr_seq_evento 
	and 	d.nr_sequencia 	= c.nr_seq_regra_dest 
	and		c.ie_funcao		= cd_funcao_ativa_p 
	and		((b.cd_pessoa_fisica = cd_pessoa_fisica_p) or (coalesce(b.cd_pessoa_fisica::text, '') = '')) 
	and		((b.nr_atendimento	= nr_atendimento_p) or (coalesce(b.nr_atendimento::text, '') = '')) 
	and		a.cd_pessoa_fisica	= cd_pessoa_fisica_w 
	and		b.dt_evento  between dt_inicial_w and dt_final_w 
	and		a.ie_status = 'G' 
	and		b.ie_status in ('G','C') 
	and		coalesce(b.ie_situacao,'A') 	= 'A' 
	and		coalesce(a.dt_ciencia::text, '') = '' 
	and		((d.cd_perfil = cd_perfil_w) or (coalesce(d.cd_perfil::text, '') = '')) LIMIT 1;
end if;
 
return	ie_exibe_alerta_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
