SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_exibe_anes ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_w 	varchar(1) := 'N';
qt_regras_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_regras_w
from	perfil_agente_anestesico
where	cd_perfil = obter_perfil_ativo;

if (qt_regras_w > 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_exibe_w
	from	perfil_agente_anestesico b,
		agente_anestesico a
	where 	a.nr_sequencia = b.nr_seq_agente
	and 	a.nr_sequencia = nr_sequencia_p
	and 	((cd_perfil = obter_perfil_ativo) or (coalesce(cd_perfil::text, '') = ''));
else
	ie_exibe_w := 'S';
end if;

return	ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
