CREATE OR REPLACE FUNCTION obter_se_exibe_anexo ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
nr_seq_status_w		bigint;
cd_setor_atendimento_w	integer;
cd_pessoa_fisica_w	varchar(10);
ie_exibe_w		varchar(1);
								

BEGIN 
 
select	max(nr_seq_status) 
into STRICT	nr_seq_status_w 
from	anexo_agenda 
where	nr_sequencia = nr_sequencia_p;
 
select	obter_dados_usuario_opcao(nm_usuario_p,'S'), 
	obter_dados_usuario_opcao(nm_usuario_p,'C') 
into STRICT	cd_setor_atendimento_w, 
	cd_pessoa_fisica_w
;
 
select	coalesce(max('N'),'S') 
into STRICT	ie_exibe_w 
from	vis_status_anexo_agenda 
where	nr_seq_status_agenda = nr_seq_status_w;
 
if (ie_exibe_w = 'N') then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_exibe_w 
		from	vis_status_anexo_agenda 
		where	coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_w,0)) 	= coalesce(cd_setor_atendimento_w,0) 
		and	coalesce(cd_pessoa_fisica,coalesce(cd_pessoa_fisica_w,0)) 		= coalesce(cd_pessoa_fisica_w,0) 
		and	nr_seq_status_agenda 				= nr_seq_status_w;
end if;
 
return	ie_exibe_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
