CREATE OR REPLACE FUNCTION obter_se_exibe_classif_pan (nr_seq_classif_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_panorama_w		varchar(1);


BEGIN

if (nr_seq_classif_p IS NOT NULL AND nr_seq_classif_p::text <> '') then

	select	ie_exibe_panorama
	into STRICT	ie_exibe_panorama_w
