CREATE OR REPLACE FUNCTION obter_se_exibe_conv_titular (cd_convenio_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_liberado_w			varchar(1);
ie_regra_exibe_conv_w	varchar(1);


BEGIN 
 
ie_liberado_w:= 'S';
 
select 	coalesce(max(ie_regra_exibe_conv),'N') 
into STRICT	ie_regra_exibe_conv_w 
from 	parametro_faturamento 
where 	cd_estabelecimento = cd_estabelecimento_p;
 
if (ie_regra_exibe_conv_w = 'S') then 
 
	begin 
	select 	coalesce(ie_exibe_titular_conv,'S') 
	into STRICT	ie_liberado_w 
	from 	convenio_estabelecimento 
	where 	cd_convenio = cd_convenio_p 
	and 	cd_estabelecimento = cd_estabelecimento_p;
	exception 
		when others then 
		ie_liberado_w:= 'N';
	end;
	 
end if;
 
return	ie_liberado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
