CREATE OR REPLACE FUNCTION obter_se_exibe_dados_pepo ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint, nr_seq_pepo_param_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_w	varchar(1);


BEGIN

if (coalesce(nr_seq_pepo_p,0) > 0) and (nr_seq_pepo_p = nr_seq_pepo_param_p) then
	ie_exibe_w := 'S';
elsif (coalesce(nr_cirurgia_p,0) > 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_exibe_w
	from	cirurgia
	where	nr_cirurgia	=	nr_cirurgia_p
	and	nr_seq_pepo	=	nr_seq_pepo_param_p;
end if;

return ie_exibe_w;

end;
$body$
