SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_exibe_elemento ( nr_seq_template_p bigint, nr_seq_elemento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_w			varchar(1) := 'S';
ie_insercao_apap_w	ehr_template.ie_insercao_apap%type := 'N';
			

BEGIN

if (nr_seq_template_p IS NOT NULL AND nr_seq_template_p::text <> '') and (nr_seq_elemento_p IS NOT NULL AND nr_seq_elemento_p::text <> '') then
	select	coalesce(max(ie_insercao_apap),'N')
	into STRICT	ie_insercao_apap_w
	from	ehr_template
	where	nr_sequencia = nr_seq_template_p;
	
	if (ie_insercao_apap_w = 'S') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_exibe_w
		from	ehr_elemento a,
				ehr_tipo_dado b
		where	a.nr_seq_tipo_dado 	= b.nr_sequencia
		and		a.nr_sequencia 		= nr_seq_elemento_p
		and		b.ds_openehr	in ('DV_TIME','DV_DATE','DV_DATE_TIME','DV_BOOLEAN','DV_TEXT','DV_COUNT','DV_QUANTITY');
	end if;	
end if;

return	ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
