CREATE OR REPLACE FUNCTION obter_se_exibe_equip (cd_equipamento_p text) RETURNS varchar AS $body$
DECLARE

ie_exibe_equip_w  		varchar(1);
ie_filtra_estabelecimento_w	varchar(1);

BEGIN

ie_filtra_estabelecimento_w := Obter_Param_Usuario(873, 26, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_filtra_estabelecimento_w);

if (ie_filtra_estabelecimento_w = 'S') then
	select 	coalesce(max('S'),'N')
	into STRICT	ie_exibe_equip_w
	from	equipamento
	where 	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
	and 	cd_equipamento = cd_equipamento_p
	and 	ie_situacao = 'A';
else
	ie_exibe_equip_w := 'S';
end if;

return	ie_exibe_equip_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
