CREATE OR REPLACE FUNCTION obter_se_exibe_evo_suep ( nr_atendimento_p bigint, cd_evolucao_p bigint, nr_seq_suep_p bigint) RETURNS varchar AS $body$
DECLARE


ie_apresenta_w			varchar(1);
ie_evolucao_clinica_w		informacao_suep.ie_evolucao_clinica%type;

C01 CURSOR FOR
	SELECT 	d.ie_evolucao_clinica
	from   	suep a,
		suep_grupo b,
		item_suep c,
		informacao_suep d
	where  	a.nr_sequencia = b.nr_seq_suep
	and	c.nr_seq_grupo = b.nr_sequencia
	and	d.nr_seq_item = c.nr_sequencia
	and	a.nr_sequencia = nr_seq_suep_p
	and	c.ie_tipo_item = 'EV';


BEGIN

open C01;
loop
fetch C01 into
	ie_evolucao_clinica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

		select	coalesce(max('S'),'N') ie_apresenta
		into STRICT	ie_apresenta_w
		from	evolucao_paciente a
		where	a.nr_atendimento = nr_atendimento_p
		and	a.cd_evolucao    = cd_evolucao_p
		and	((a.ie_evolucao_clinica = ie_evolucao_clinica_w) or (coalesce(ie_evolucao_clinica_w::text, '') = ''));

		if (ie_apresenta_w = 'S') then
			exit;
		end if;

	end;
end loop;
close C01;



return	ie_apresenta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
