CREATE OR REPLACE FUNCTION obter_se_exibe_gas ( ie_respiracao_p text, nr_seq_gas_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibir_w	varchar(1) := 'S';
cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	regra_respiracao_gas
where	ie_respiracao	= ie_respiracao_p;

if (cont_w	> 0) then
	select	count(*)
	into STRICT	cont_w
	from	regra_respiracao_gas
	where	nr_seq_gas	= nr_seq_gas_p
	and	ie_respiracao	= ie_respiracao_p;

	ie_exibir_w	:= 'S';
	if (cont_w = 0) then
		ie_exibir_w	:= 'N';
	end if;
end if;

return	ie_exibir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
