CREATE OR REPLACE FUNCTION obter_se_exibe_icone_mapa (cd_icone_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_w		regra_perm_panorama.nr_sequencia%type;
ie_exibe_icone_w	varchar(1) := 'S';


BEGIN

select	max(a.nr_sequencia)
into STRICT 	nr_seq_regra_w
from	regra_perm_panorama a
where	a.cd_perfil = cd_perfil_p
and exists (	SELECT 1
		from	regra_perm_panorama_icone x
		where	x.nr_seq_regra = a.nr_sequencia);

if (coalesce(nr_seq_regra_w::text, '') = '') then -- se n√£o encontrou a regra para o perfil, busca uma regra sem perfil definido.
	select	max(a.nr_sequencia)
	into STRICT 	nr_seq_regra_w
	from	regra_perm_panorama a
	where	coalesce(a.cd_perfil::text, '') = ''
	and exists (	SELECT 1
			from	regra_perm_panorama_icone x
			where	x.nr_seq_regra = a.nr_sequencia);
end if;

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_exibe_icone_w
	from	regra_perm_panorama_icone
	where	nr_seq_regra = nr_seq_regra_w
	and	cd_icone_panorama = cd_icone_p;
end if;

return ie_exibe_icone_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
