SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_exibe_incons_gpt ( nr_seq_item_p bigint, nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
nr_prescricao_w		bigint;
qt_registro_w		bigint;


BEGIN


if (nm_tabela_p = 'SOLUCAO') then
	nr_prescricao_w := obter_prescr_item_cpoe(nr_seq_item_p,cpoe_obter_tipo_item('CPOE_MATERIAL',nr_seq_item_p));
else
	nr_prescricao_w := obter_prescr_item_cpoe(nr_seq_item_p,cpoe_obter_tipo_item(nm_tabela_p,nr_seq_item_p));
end if;

	if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
		begin

			if ('CPOE_DIALISE' = nm_tabela_p) then
				begin
					select	count(*) quantidade
					into STRICT 	qt_registro_w
					from	prescr_material_incon_farm
					where	nr_prescricao 	= nr_prescricao_w
					and ((nr_seq_solucao IS NOT NULL AND nr_seq_solucao::text <> '')
					or 		(nr_seq_material IS NOT NULL AND nr_seq_material::text <> ''))
					and 	ie_situacao = 'A';
				end;
			elsif ('CPOE_MATERIAL' = nm_tabela_p) then
				begin
					select	count(*) quantidade
					into STRICT	qt_registro_w
					from   	prescr_material_incon_farm
					where  	nr_prescricao 	= nr_prescricao_w
					and		nr_seq_material	in (SELECT nr_sequencia from prescr_material where nr_seq_mat_cpoe = nr_seq_item_p and nr_prescricao = nr_prescricao_w)
					and 	ie_situacao = 'A';
				end;
			elsif ('SOLUCAO' = nm_tabela_p) then
				begin
					select	count(*) quantidade
					into STRICT	qt_registro_w
					from   	prescr_material_incon_farm
					where  	nr_prescricao 	= nr_prescricao_w
					and		nr_seq_solucao	in (SELECT nr_sequencia_solucao from prescr_material where nr_seq_mat_cpoe = nr_seq_item_p and nr_prescricao = nr_prescricao_w)
					and 	ie_situacao = 'A';
				end;
			end if;

		if (qt_registro_w > 0) then
			ds_retorno_w := 'S';
		end if;

		end;
	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
