CREATE OR REPLACE FUNCTION obter_se_exibe_indicacao ( nr_sequencia_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1) := 'S';
ie_existe_regra_w		varchar(1);

c01 CURSOR FOR
SELECT	coalesce(a.ie_visualizar,'S')
FROM san_indicacao_regra a
LEFT OUTER JOIN san_indicacao b ON (a.nr_seq_regra = b.nr_sequencia)
WHERE coalesce(b.ie_situacao,'A')	= 'A' and b.nr_sequencia = nr_sequencia_p and (a.cd_perfil = cd_perfil_p or coalesce(a.cd_perfil::text, '') = '') order by a.cd_perfil;


BEGIN

select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_existe_regra_w
from	san_indicacao_regra
where	cd_perfil = cd_perfil_p;



if (ie_existe_regra_w = 'S') then
	open C01;
	loop
	fetch C01 into
		ie_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ie_retorno_w	:= ie_retorno_w;
	end loop;
	close C01;
else
	ie_retorno_w := 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
