SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_exibe_int_medic ( nr_prescricao_p bigint, ds_severidades_p text, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	char(1) := 'N';

BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	prescr_medica_interacao where	nr_prescricao	= nr_prescricao_p
	and	obter_se_valor_contido(ie_Severidade,ds_severidades_p) = 'S'
	and	((coalesce(cd_material_p::text, '') = '') or (cd_material_p in (cd_material_interacao, cd_material))) LIMIT 1;


	if (ds_retorno_w = 'N') then

		if (coalesce(cd_material_p::text, '') = '') then

				select coalesce(max('S'),'N')
				into STRICT	 ds_retorno_w
				from	 prescr_material a,
						 prescr_dieta b
				where  a.nr_prescricao = b.nr_Prescricao
				and	 a.nr_prescricao = nr_Prescricao_p
				and	 coalesce(a.dt_suspensao::text, '') = ''
				and	 exists( SELECT 1
									from medic_interacao_alimento c
									where c.cd_material = a.cd_material
									and	((coalesce(c.cd_dieta::text, '') = '') or (c.cd_dieta = b.cd_dieta)));
		else
			select coalesce(max('S'),'N')
			into STRICT	 ds_retorno_w
			from	 medic_interacao_alimento a
			where	 cd_material	= cd_material_p
			and	 ((coalesce(cd_dieta::text, '') = '') or (exists (SELECT	1
													from	prescr_dieta b
													where	b.nr_Prescricao	= nr_prescricao_p
													and	coalesce(b.dt_suspensao::text, '') = ''
													and		b.cd_dieta		= a.cd_dieta)));
		end if;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
