CREATE OR REPLACE FUNCTION obter_se_exibe_item_legenda (nr_seq_cor_p bigint, nr_seq_imagem_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


c01 CURSOR FOR
SELECT	ie_utiliza
from	tasy_regra_legenda_cliente
where	nr_seq_cor					= nr_seq_cor_p
and	coalesce(cd_estabelecimento,cd_estabelecimento_p) 	= cd_estabelecimento_p
and	coalesce(cd_perfil,cd_perfil_p) 			= cd_perfil_p
and	coalesce(nm_usuario_regra,nm_usuario_p) 		= nm_usuario_p
order by coalesce(nm_usuario_regra,null) desc,
	coalesce(cd_perfil,0),
	coalesce(cd_estabelecimento,0);

c02 CURSOR FOR
SELECT	ie_utiliza
from	tasy_regra_legenda_cliente
where	nr_seq_imagem					= nr_seq_imagem_p
and	coalesce(cd_estabelecimento,cd_estabelecimento_p) 	= cd_estabelecimento_p
and	coalesce(cd_perfil,cd_perfil_p) 			= cd_perfil_p
and	coalesce(nm_usuario_regra,nm_usuario_p) 		= nm_usuario_p
order by coalesce(nm_usuario_regra,null) desc,
	coalesce(cd_perfil,0),
	coalesce(cd_estabelecimento,0);

ie_utilizar_w	varchar(1) := 'S';
qt_w		bigint;


BEGIN

if (coalesce(nr_seq_cor_p,0) > 0) then
	begin
	select	1
	into STRICT	qt_w
	from	tasy_regra_legenda_cliente
	where	nr_seq_cor	= nr_seq_cor_p  LIMIT 1;
	exception
	when others then
		qt_w := 0;
	end;

	if (qt_w > 0) then

		open C01;
		loop
		fetch C01 into
			ie_utilizar_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		end loop;
		close C01;

	end if;

elsif (coalesce(nr_seq_imagem_p,0) > 0) then
	begin
	select	1
	into STRICT	qt_w
	from	tasy_regra_legenda_cliente
	where	nr_seq_imagem	= nr_seq_imagem_p  LIMIT 1;
	exception
	when others then
		qt_w := 0;
	end;

	if (qt_w > 0) then
		open C02;
		loop
		fetch C02 into
			ie_utilizar_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
		end loop;
		close C02;
	end if;

end if;

if (qt_w > 0) then
	return	ie_utilizar_w;
else
	return	'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
