CREATE OR REPLACE FUNCTION obter_se_exibe_itens_gpt ( ds_itens_p text, nr_atendimento_p bigint, ie_tipo_gpt_p text) RETURNS varchar AS $body$
DECLARE

 
ds_lista_w		varchar(1000);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
ie_item_w		varchar(10);
ie_item_valido_w	varchar(1) := 'N';


BEGIN 
 
ds_lista_w := ds_itens_p;
 
if (substr(ds_lista_w,length(ds_lista_w) - 1, length(ds_lista_w))	<> ',') then 
	ds_lista_w	:= ds_lista_w ||',';
end if;
 
while((ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') and ie_item_valido_w = 'N') loop 
	begin 
 
	tam_lista_w		:= length(ds_lista_w);
	ie_pos_virgula_w	:= position(',' in ds_lista_w);
	 
	if (ie_pos_virgula_w <> 0) then 
		ie_item_w		:= substr(ds_lista_w,1,(ie_pos_virgula_w - 1));
		ds_lista_w		:= substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;
	 
	ie_item_valido_w := obter_itens_gpt(nr_atendimento_p,ie_tipo_gpt_p,ie_item_w);
	 
	end;
	 
end loop;
 
return	ie_item_valido_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
