CREATE OR REPLACE FUNCTION obter_se_exibe_lado_proc ( cd_procedimento_p bigint, ie_origem_proced_p text, ie_lado_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'S';
cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from 	rep_regra_lado_proced
where	cd_procedimento  = cd_procedimento_p
and	ie_origem_proced = ie_origem_proced_p;

if (cont_w	> 0) then

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from 	rep_regra_lado_proced
	where	cd_procedimento  = cd_procedimento_p
	and	ie_origem_proced = ie_origem_proced_p
	and	ie_lado 	= ie_lado_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
