CREATE OR REPLACE FUNCTION obter_se_exibe_leito_panorama ( nr_seq_interno_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_w		regra_perm_panorama.nr_sequencia%type;
ie_status_unidade_w	unidade_atendimento.ie_status_unidade%type;
ie_possui_acomodacao_w	varchar(1);
ie_exibe_leito_w	varchar(1) := 'S';


BEGIN

select	ie_status_unidade,
	CASE WHEN obter_se_sem_acomodacao(cd_tipo_acomodacao)='S' THEN 'N'  ELSE 'S' END  ie_possui_acomodacao
into STRICT	ie_status_unidade_w,
	ie_possui_acomodacao_w
from	unidade_atendimento
where	nr_seq_interno = nr_seq_interno_p;

select	max(a.nr_sequencia)
into STRICT 	nr_seq_regra_w
from	regra_perm_panorama a
where	a.cd_perfil = cd_perfil_p
and exists (	SELECT	1
		from	regra_perm_panorama_status x
		where	x.nr_seq_regra = a.nr_sequencia);

if (coalesce(nr_seq_regra_w::text, '') = '') then -- se n√£o encontrou a regra para o perfil, busca uma regra sem perfil definido.
	select	max(a.nr_sequencia)
	into STRICT 	nr_seq_regra_w
	from	regra_perm_panorama a
	where	coalesce(a.cd_perfil::text, '') = ''
	and exists (	SELECT	1
			from	regra_perm_panorama_status x
			where	x.nr_seq_regra = a.nr_sequencia);
end if;

if	(nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '' AND ie_possui_acomodacao_w = 'S') then
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_exibe_leito_w
	from	regra_perm_panorama_status
	where	nr_seq_regra = nr_seq_regra_w
	and	ie_status_leito = ie_status_unidade_w;
end if;

return ie_exibe_leito_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
