CREATE OR REPLACE FUNCTION obter_se_exibe_linha_cuidado (nr_seq_linha_cuidado_p PROTOCOLO_INTEGRADO.NR_SEQUENCIA%TYPE, cd_pessoa_fisica_p PESSOA_FISICA.cd_pessoa_fisica%TYPE) RETURNS varchar AS $body$
DECLARE


ie_exibe_cid_w		varchar(1) := 'S';
ie_exibe_proc_w		varchar(1) := 'S';
qt_cid_w      integer;
qt_proc_w      integer;

BEGIN

SELECT COUNT(*)
into STRICT qt_cid_w
FROM protocolo_integrado_doenca
WHERE nr_seq_protocolo_integrado = nr_seq_linha_cuidado_p;

SELECT COUNT(*)
into STRICT qt_proc_w
FROM protocolo_integrado_proc
WHERE nr_seq_protocolo_integrado = nr_seq_linha_cuidado_p;

if ( qt_cid_w != 0 ) then
  SELECT CASE WHEN coalesce(max(protocolo_integrado_doenca.nr_sequencia), 0)=0 THEN  'N'  ELSE 'S' END
  into STRICT ie_exibe_cid_w
  FROM
      protocolo_integrado,
      protocolo_integrado_doenca,
      diagnostico_doenca
  WHERE 
      protocolo_integrado.nr_sequencia = nr_seq_linha_cuidado_p
  AND protocolo_integrado.nr_sequencia = protocolo_integrado_doenca.nr_seq_protocolo_integrado
  AND protocolo_integrado_doenca.cd_doenca_cid = diagnostico_doenca.cd_doenca
  AND diagnostico_doenca.nr_atendimento IN (
          SELECT atendimento_paciente.nr_atendimento
          FROM atendimento_paciente
          WHERE atendimento_paciente.cd_pessoa_fisica = cd_pessoa_fisica_p);
end if;

if ( qt_proc_w != 0 ) then
  SELECT CASE WHEN coalesce(max(protocolo_integrado_proc.nr_sequencia), 0)=0 THEN  'N'  ELSE 'S' END
  into STRICT ie_exibe_proc_w
  FROM
      protocolo_integrado,
      protocolo_integrado_proc,
      proc_pac_descricao
  WHERE 
      protocolo_integrado.nr_sequencia = nr_seq_linha_cuidado_p
  AND protocolo_integrado.nr_sequencia = protocolo_integrado_proc.nr_seq_protocolo_integrado
  AND protocolo_integrado_proc.nr_seq_proc_interno = proc_pac_descricao.nr_seq_proc_interno
  AND proc_pac_descricao.cd_pessoa_fisica = cd_pessoa_fisica_p;
end if;

if ( ie_exibe_cid_w = 'S' and ie_exibe_proc_w = 'S' ) then
  return 'S';
else
  return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
