CREATE OR REPLACE FUNCTION obter_se_exibe_local_pa_adep (cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1) := 'N';


BEGIN

if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then
	select  coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from 	setor_atendimento
	where 	cd_setor_atendimento = cd_setor_atendimento_p
	and	cd_classif_setor = '1';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
