CREATE OR REPLACE FUNCTION obter_se_exibe_med_exec (nr_seq_turno_p bigint, hr_agenda_p timestamp, cd_agenda_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


qt_permite_w		bigint;
qt_nao_permite_w	bigint;
ds_retorno_w		varchar(1) := 'S';


BEGIN
select  count(*)
into STRICT	qt_nao_permite_w
from    agenda_medico
where   cd_agenda = cd_agenda_p
and	cd_medico = cd_medico_p
and     ie_situacao = 'A'
and     coalesce(ie_permite,'S') = 'N'
and     ((coalesce(nr_seq_turno,0) = nr_seq_turno_p)  or (coalesce(nr_seq_turno,0) = 0))
and     ((hr_agenda_p between dt_inicio_vigencia and dt_fim_vigencia) or
	((coalesce(dt_inicio_vigencia::text, '') = '') and (coalesce(dt_fim_vigencia::text, '') = '')) or
	((hr_agenda_p >= dt_inicio_vigencia) and (coalesce(dt_fim_vigencia::text, '') = '')) or
	((hr_agenda_p <= dt_fim_vigencia) and (coalesce(dt_inicio_vigencia::text, '') = '')));

if (qt_nao_permite_w = 0) then
	select  count(*)
	into STRICT	qt_permite_w
	from    agenda_medico
	where   cd_agenda = cd_agenda_p
	and	cd_medico = cd_medico_p
	and     ie_situacao = 'A'
	and     coalesce(ie_permite,'S') = 'S'
	and     ((coalesce(nr_seq_turno,0) = nr_seq_turno_p)  or (coalesce(nr_seq_turno,0) = 0))
	and     ((hr_agenda_p between dt_inicio_vigencia and dt_fim_vigencia) or
		((coalesce(dt_inicio_vigencia::text, '') = '') and (coalesce(dt_fim_vigencia::text, '') = '')) or
		((hr_agenda_p >= dt_inicio_vigencia) and (coalesce(dt_fim_vigencia::text, '') = '')) or
		((hr_agenda_p <= dt_fim_vigencia) and (coalesce(dt_inicio_vigencia::text, '') = '')));
end if;

if (qt_nao_permite_w > 0) then
	ds_retorno_w := 'N';
elsif (qt_permite_w = 0) then
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
