CREATE OR REPLACE FUNCTION obter_se_exibe_motivo_rep ( ie_motivo_prescricao_p text, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibir_w	varchar(1) := 'S';
ie_funcao_prescritor_w varchar(3);

c01 CURSOR FOR
SELECT	ie_exibir
from	rep_exibir_motivo_prescr
where	coalesce(ie_funcao_prescritor,ie_funcao_prescritor_w)	= ie_funcao_prescritor_w
and	coalesce(cd_perfil,cd_perfil_p)				= cd_perfil_p
and	coalesce(ie_motivo_prescricao,ie_motivo_prescricao_p)	= ie_motivo_prescricao_p
order by coalesce(cd_perfil,0),
	 coalesce(ie_motivo_prescricao,'0');


BEGIN

Select coalesce(MAX(IE_TIPO_EVOLUCAO),0)
into STRICT	ie_funcao_prescritor_w
from	usuario
where	nm_usuario = wheb_usuario_pck.get_nm_usuario;

open C01;
loop
fetch C01 into
	ie_exibir_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_exibir_w	:= ie_exibir_w;
end loop;
close C01;

return	ie_exibir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
