SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_exibe_nao_conf_gpt (nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(1) := 'N';
qt_registro_w		bigint;
nr_prescricao_w		bigint;


BEGIN 
 
nr_prescricao_w := obter_prescr_item_cpoe(nr_seq_item_p,cpoe_obter_tipo_item('CPOE_MATERIAL',nr_seq_item_p));
 
SELECT	count(*) 
into STRICT 	qt_registro_w 
FROM	prescr_material a, 
	prescr_medica_erro b 
WHERE	a.nr_prescricao = b.nr_prescricao 
AND	b.nr_seq_medic = a.nr_sequencia 
AND	a.nr_prescricao = nr_prescricao_w 
AND	b.nr_seq_medic in (SELECT y.nr_sequencia 
				from 	prescr_material y 
				where 	nr_seq_mat_cpoe = nr_seq_item_p) 
AND		 a.ie_agrupador = 1;
 
if (qt_registro_w > 0) then 
	ds_retorno_w := 'S';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
