CREATE OR REPLACE FUNCTION obter_se_exibe_os_confind ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

nr_seq_confidencialidade_w	bigint;
ie_mostrar_os_w	varchar(1);
ds_texto_w	varchar(4000) := '';

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select 	coalesce(nr_seq_confidencialidade,0)
	into STRICT	nr_seq_confidencialidade_w
	from 	man_ordem_servico
	where 	nr_sequencia = nr_sequencia_p;
	if (nr_seq_confidencialidade_w <> 0)
		and (nr_seq_confidencialidade_w <> '') then
		select 	coalesce(substr(man_obter_se_mostra_os_confid(nr_sequencia_p, nm_usuario_p),1,1),'S')
		into STRICT	ie_mostrar_os_w
		;
		if (ie_mostrar_os_w = 'N') then
			/* Esta OS possui uma regra de confidencialidade que não é permitida para seu usuário. */

			ds_texto_w := obter_texto_tasy(225280, 1);
		end if;
	end if;
end if;
return	ds_texto_w;
end;
$body$
LANGUAGE PLPGSQL
