SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_exibe_presc_item_susp (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_susp_w		bigint;
qt_nao_suspensos_w	bigint;
ds_retorno_w		varchar(1) := 'N';


BEGIN
	--Verifica se exitem itens suspensos na prescrição.
	select 	sum(qt_susp)
	into STRICT	qt_susp_w
	from (	SELECT		count(*) qt_susp
			from		prescr_material
			where		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			and		nr_prescricao	= nr_prescricao_p
			
union

			SELECT		count(*) qt_susp
			from		prescr_procedimento
			where		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			and		nr_prescricao	= nr_prescricao_p
			
union

			select		count(*) qt_susp
			from		prescr_dieta
			where		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			and		nr_prescricao	= nr_prescricao_p
			
union

			select		count(*) qt_susp
			from		rep_jejum
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'S'
			
union

			select		count(*) qt_susp
			from		nut_pac
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			
union

			select		count(*) qt_susp
			from		prescr_solucao
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			
union

			select		count(*) qt_susp
			from		prescr_gasoterapia
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')
			
union

			select		count(*) qt_susp
			from		prescr_recomendacao
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'S'
			and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '')) alias24;

	--Verifica se existem itens na prescrição não suspensos.
	select 	sum(qt)
	into STRICT	qt_nao_suspensos_w
	from (	SELECT		count(*) qt
			from		prescr_material
			where		coalesce(ie_suspenso,'N')	= 'N'
			and		coalesce(dt_suspensao::text, '') = ''
			and		nr_prescricao	= nr_prescricao_p
			
union

			SELECT		count(*) qt
			from		prescr_procedimento
			where		coalesce(ie_suspenso,'N')	= 'N'
			and		coalesce(dt_suspensao::text, '') = ''
			and		nr_prescricao	= nr_prescricao_p
			
union

			select		count(*) qt
			from		prescr_dieta
			where		coalesce(ie_suspenso,'N')	= 'N'
			and		coalesce(dt_suspensao::text, '') = ''
			and		nr_prescricao	= nr_prescricao_p
			
union

			select		count(*) qt
			from		rep_jejum
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'N'
			
union

			select		count(*) qt
			from		nut_pac
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'N'
			
union

			select		count(*) qt
			from		prescr_solucao
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'N'
			and		coalesce(dt_suspensao::text, '') = ''
			
union

			select		count(*) qt
			from		prescr_gasoterapia
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'N'
			and		coalesce(dt_suspensao::text, '') = ''
			
union

			select		count(*) qt
			from		prescr_recomendacao
			where		nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso,'N')	= 'N'
			and		coalesce(dt_suspensao::text, '') = '') alias23;


if (qt_nao_suspensos_w = 0) and (qt_susp_w = 0)	then
	ds_retorno_w	:= 'S'; --Não considerar essa prescrição pois não tem itens
elsif (qt_nao_suspensos_w > 0) then
	ds_retorno_w	:= 'N'; --Considerar essa prescrição pois existe itens nao suspensos.
elsif (qt_susp_w > 0)	 then
	ds_retorno_w	:= 'S'; --Não considerar essa prescrição pois so existem itens suspensos nela.
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
