CREATE OR REPLACE FUNCTION obter_se_exibe_produto ( nr_seq_produto_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, nr_seq_agrup_setor_p bigint, cd_setor_atendimento_p bigint, cd_convenio_p bigint default null) RETURNS varchar AS $body$
