CREATE OR REPLACE FUNCTION obter_se_exibe_prot_peritoneal ( ie_tipo_solucao_p text, ie_tipo_solucao_prot_p text, ie_tipo_peritoneal_p text, ie_destino_p bigint, nr_seq_dialise_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_w			varchar(1) := 'N';
ie_tipo_peritoneal_w		varchar(15);

BEGIN

select	max(ie_tipo_peritoneal)
into STRICT	ie_tipo_peritoneal_w
from	protocolo_hd_prescricao
where	nr_sequencia	= nr_seq_dialise_p;

if (ie_tipo_peritoneal_w IS NOT NULL AND ie_tipo_peritoneal_w::text <> '') and (ie_destino_p = 3) and
	((ie_tipo_peritoneal_w = ie_tipo_peritoneal_p) or (coalesce(ie_tipo_peritoneal_p::text, '') = '')) then
	ie_exibe_w	:= 'S';
elsif (coalesce(ie_tipo_peritoneal_w::text, '') = '') and (ie_destino_p = 2) and
	((ie_tipo_solucao_p = ie_tipo_solucao_prot_p) or (coalesce(ie_tipo_solucao_prot_p::text, '') = '')) then
	ie_exibe_w	:= 'S';
end if;

return ie_exibe_w;

end;
$body$
