CREATE OR REPLACE FUNCTION obter_se_exibe_receita (nr_seq_grupo_p bigint, nr_seq_composicao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_w 	varchar(1);
ie_parametro_w	varchar(1);


BEGIN

ie_parametro_w := obter_param_usuario(912, 18, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_parametro_w);

if (ie_parametro_w = 'S') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select  coalesce(max('S'),'N')
	into STRICT	ie_exibe_w
	from    nut_receita
	where (nr_seq_grupo 		= nr_seq_grupo_p or coalesce(nr_seq_grupo_p::text, '') = '')
	and (nr_seq_composicao 	= nr_seq_composicao_p)
	and	nr_sequencia		= nr_sequencia_p;
else
	ie_exibe_w := 'S';
end if;

return	ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
