CREATE OR REPLACE FUNCTION obter_se_exibe_regra_copia ( nr_seq_regra_p bigint, cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(10) := 'S';
cont_w		bigint;


BEGIN 
 
select	count(*) 
into STRICT	cont_w 
from	regra_copia_rep_perfil 
where	nr_seq_regra	= nr_seq_regra_p;
 
if (cont_w	> 0) then 
	select	count(*) 
	into STRICT	cont_w 
	from	regra_copia_rep_perfil 
	where	nr_seq_regra	= nr_seq_regra_p 
	and	((cd_perfil	= cd_perfil_p) or (coalesce(cd_perfil::text, '') = '')) 
	and	((nm_usuario_regra	= nm_usuario_p) or (coalesce(nm_usuario_regra::text, '') = ''))	 
	and	((coalesce(cd_estab::text, '') = '') or (cd_estab = cd_estabelecimento_p)) 
	and	((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(obter_pessoa_fisica_usuario(nm_usuario_p, 'C'), cd_especialidade) = 'S'));	
		 
	if (cont_w	= 0) then 
		ds_retorno_w	:= 'N';
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
