CREATE OR REPLACE FUNCTION obter_se_exibe_reserva ( nr_seq_servico_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, nr_seq_reserva_p bigint, ie_periodo_p text default 'N') RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(1);



BEGIN

if (ie_periodo_p = 'N') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	nut_reserva
	where	nr_sequencia	= nr_seq_reserva_p
	and	nr_seq_servico	= nr_seq_servico_p
	and	to_date(to_char(dt_servico,'dd/mm/yyyy') || ' ' || to_char(hr_inicio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') > clock_timestamp();
elsif (ie_periodo_p = 'S') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	nut_reserva
	where	nr_sequencia	= nr_seq_reserva_p
	and	nr_seq_servico	= nr_seq_servico_p
	and	dt_servico between inicio_dia(dt_inicial_p) and fim_dia(dt_final_p);
end if;
return	ds_retorno_w;

end;
