CREATE OR REPLACE FUNCTION obter_se_exibe_rotina ( nr_sequencia_p bigint, cd_medico_p text, cd_estabelecimento_p bigint default 0, nr_seq_agrupamento_p bigint default null, cd_setor_atendimento_p bigint default null) RETURNS varchar AS $body$
DECLARE

 
ie_exibir_w				char(1) := 'S';
cd_perfil_w				perfil.cd_perfil%type;
cd_setor_atendimento_w	setor_atendimento.cd_setor_atendimento%type;
nr_seq_agrupamento_w	setor_atendimento.nr_seq_agrupamento%type;

c02 CURSOR FOR 
	SELECT	coalesce(ie_incluir,'N') 
	from	rotina_regra 
	where	nr_seq_rotina	= nr_sequencia_p 
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p 
	and	coalesce(nr_seq_agrupamento,coalesce(nr_seq_agrupamento_p,0)) = coalesce(nr_seq_agrupamento_p,0) 
	and	coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0) 
	and	coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w	 
	order by coalesce(cd_perfil,0), coalesce(nr_seq_agrupamento,0), coalesce(cd_estabelecimento,0);

BEGIN
 
select	coalesce(max('N'),'S') 
into STRICT	ie_exibir_w 
from	exame_lab_rotina where		nr_seq_rotina	= nr_sequencia_p LIMIT 1;
 
if (ie_exibir_w = 'S') then 
	select	coalesce(max('N'),'S') 
	into STRICT	ie_exibir_w 
	from	procedimento_rotina where		nr_seq_rotina	= nr_sequencia_p LIMIT 1;
end if;
 
if (ie_exibir_w = 'N') then 
	select	coalesce(max('N'),'S') 
	into STRICT	ie_exibir_w 
	from	rotina_especialidade 
	where	nr_seq_rotina	= nr_sequencia_p;
 
	if (ie_exibir_w = 'N') then	 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_exibir_w 
		from	rotina_especialidade b, 
				medico_especialidade a where		a.cd_pessoa_fisica	= cd_medico_p 
		and		a.cd_especialidade = b.cd_especialidade 
		and		b.nr_seq_rotina		= nr_sequencia_p LIMIT 1;
	end if;
	 
	if (ie_exibir_w = 'S') then 
	 
		select	coalesce(max('N'),'S') 
		into STRICT	ie_exibir_w 
		from	rotina_regra where		nr_seq_rotina	= nr_sequencia_p LIMIT 1;
		 
		if (ie_exibir_w = 'N') then 
			cd_perfil_w				:= coalesce(obter_perfil_ativo,0);
			cd_setor_atendimento_w	:= coalesce(cd_setor_atendimento_p,0);
			nr_seq_agrupamento_w	:= coalesce(nr_seq_agrupamento_p,0);
			 
			select	coalesce(max('S'),'N') 
			into STRICT	ie_exibir_w 
			from	rotina_regra where		nr_seq_rotina	= nr_sequencia_p 
			and		coalesce(ie_incluir,'N') = 'S' 
			and		coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p 
			and		coalesce(nr_seq_agrupamento,nr_seq_agrupamento_w) = nr_seq_agrupamento_w 
			and		coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w 
			and		coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w LIMIT 1;
		end if;
	end if;	
end if;
 
return	ie_exibir_w;
 
END;
$body$
