CREATE OR REPLACE FUNCTION obter_se_exibe_solic_exame ( nr_prontuario_p bigint, cd_setor_atendimento_p bigint, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(1);
qt_reg_w	integer;
/*
  Opções de retorno
  N : Não achou nenhum, nem para a data corrente e nem para datas futuras
  C : Achou mais que uma para a data corrente
  S : Achou uma para a data corrente
  R : Achou uma ou mais que uma para a data igual ou superior a corrente
*/
BEGIN
select	count(*)
into STRICT	qt_reg_w
from	exame_solicitacao a
where	a.nr_prontuario = nr_prontuario_p
and	((a.cd_setor_atendimento = cd_setor_atendimento_p)
or (cd_setor_atendimento_p = 0))
and	((a.cd_agenda = cd_agenda_p)
or (cd_agenda_p = 0))
and	trunc(a.dt_desejada,'dd') = trunc(clock_timestamp(),'dd')
and	a.ie_status_solic = 'A';
if (qt_reg_w = 1) then
	ie_retorno_w	:= 'S';
elsif (qt_reg_w > 1) then
	ie_retorno_w	:= 'C';
else
	select	count(*)
	into STRICT	qt_reg_w
	from	exame_solicitacao a
	where	a.nr_prontuario = nr_prontuario_p
	and	((a.cd_setor_atendimento = cd_setor_atendimento_p)
	or (cd_setor_atendimento_p = 0))
	and	((a.cd_agenda = cd_agenda_p)
	or (cd_agenda_p = 0))
	and	trunc(a.dt_desejada,'dd') >= trunc(clock_timestamp(),'dd')
	and	ie_status_solic = 'A';
	if (qt_reg_w >= 1) then
		ie_retorno_w	:= 'R';
	else
		ie_retorno_w	:= 'N';
	end if;
end if;
return	ie_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
