CREATE OR REPLACE FUNCTION obter_se_exibe_sol_consist ( nr_seq_dialise_p bigint, nr_seq_solucao_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(1);
cont_w bigint;


BEGIN

ds_retorno_w := 'S';

if (coalesce(nr_seq_dialise_p::text, '') = '') then
	ds_retorno_w := 'S';
else
	select	count(*)
	into STRICT	cont_w
	from	hd_prescricao
	where	nr_sequencia = nr_seq_dialise_p
	and	ie_status = 'T';

	if (cont_w > 0) then
		ds_retorno_w := 'N';
	else
		select	count(*)
		into STRICT	cont_w
		from	hd_prescricao_evento
		where	nr_prescricao = nr_prescricao_p
		and	nr_seq_solucao = nr_seq_solucao_p;

		if (cont_w > 0) then
			ds_retorno_w := 'N';
		else
			select	count(*)
			into STRICT	cont_w
			from	hd_prescricao_evento
			where	nr_prescricao = nr_prescricao_p
			and	nr_sequencia = nr_seq_dialise_p;

			if (cont_w > 0) then
				ds_retorno_w := 'N';
			end if;
		end if;
	end if;

end if;

return ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
