CREATE OR REPLACE FUNCTION obter_se_exibe_tecnica_anes ( ie_libera_anest_p bigint, nr_seq_proc_interno_p bigint, nr_seq_tecnica_anest_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_tecnica_w	varchar(1) := 'N';
ie_possui_cadastro_w	varchar(1) := 'N';
ie_tecnica_lib_w	varchar(1) := 'N';


BEGIN

if (nr_seq_proc_interno_p > 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_possui_cadastro_w
	from	proc_interno_tecnica_anest
	where	nr_seq_proc_interno	=	nr_seq_proc_interno_p;

	select	coalesce(max('S'),'N')
	into STRICT	ie_tecnica_lib_w
	from	proc_interno_tecnica_anest
	where	nr_seq_proc_interno	=	nr_seq_proc_interno_p
	and	nr_seq_tecnica_anest	=	nr_seq_tecnica_anest_p;
end if;

if (ie_libera_anest_p = 3) then
	ie_exibe_tecnica_w	:= 'S';
elsif (ie_libera_anest_p = 2) and ((ie_possui_cadastro_w = 'N') or (ie_tecnica_lib_w = 'S')) then
	ie_exibe_tecnica_w	:= 'S';
elsif (ie_libera_anest_p = 1) and (ie_tecnica_lib_w = 'S') then
	ie_exibe_tecnica_w	:= 'S';
end if;

return	ie_exibe_tecnica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
