CREATE OR REPLACE FUNCTION obter_se_exibe_tipo_card ( cd_pessoa_fisica_p text, ie_tipo_item_p text, nr_seq_modelo_princ_p bigint default null) RETURNS varchar AS $body$
DECLARE




ie_exibe_w	varchar(1) := 'S';
ie_sexo_w	varchar(1);


BEGIN

if (nr_seq_modelo_princ_p IS NOT NULL AND nr_seq_modelo_princ_p::text <> '') then

  Select coalesce(max('N'),'S')
  into STRICT  ie_exibe_w
  from  suep a,
        item_suep b
  where b.NR_SEQ_SUEP = a.nr_sequencia
  and   a.nr_SEQUENCIA = nr_seq_modelo_princ_p;

end if;

if (ie_exibe_w = 'S') and (ie_tipo_item_p in ('PA','HP','PN','SM')) then

	Select 	obter_sexo_pf(cd_pessoa_fisica_p,'C')
	into STRICT	ie_sexo_w
	;


	if (ie_sexo_w <> 'F') then
		ie_exibe_w := 'N';
	end if;

end if;


return	ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
