CREATE OR REPLACE FUNCTION obter_se_exibe_tipo_consis (nr_seq_tipo_cons_p bigint, cd_setor_atendimento_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w                    varchar(1);
qt_reg_w                        integer;
NR_SEQ_AGRUPAMENTO_W    bigint;

c01 CURSOR FOR
SELECT  coalesce(ie_mostrar,'S')
from    consistencia_dieta_regra
where   nr_seq_consistencia     = nr_seq_tipo_cons_p
and     ((coalesce(cd_setor_atendimento::text, '') = '') or (cd_setor_atendimento = cd_setor_atendimento_p))
and     ((coalesce(cd_estabelecimento::text, '') = '') or (cd_estabelecimento = cd_estabelecimento_p))
and     ((coalesce(cd_perfil::text, '') = '') or (cd_perfil = cd_perfil_p))
and     ((coalesce(cd_setor_excluir::text, '') = '') or (cd_setor_excluir <> cd_setor_atendimento_p))
AND 	((NR_SEQ_AGRUPAMENTO = NR_SEQ_AGRUPAMENTO_W) OR (coalesce(NR_SEQ_AGRUPAMENTO::text, '') = ''))
order by coalesce(NR_SEQ_AGRUPAMENTO,0),
                coalesce(cd_perfil,0),
                coalesce(cd_setor_atendimento,0),
                coalesce(cd_setor_excluir,0);




BEGIN

select  count(*)
into STRICT    qt_reg_w
from    consistencia_dieta_regra
where   nr_seq_consistencia             = nr_seq_tipo_cons_p;

ds_retorno_w    := 'S';

if (qt_reg_w > 0) then

        select  max(nr_seq_agrupamento)
        into STRICT    nr_seq_agrupamento_w
        from    setor_atendimento
        where   cd_setor_atendimento = cd_setor_atendimento_p;

        open C01;
        loop
        fetch C01 into
                ds_retorno_w;
        EXIT WHEN NOT FOUND; /* apply on C01 */
                ds_retorno_w    := ds_retorno_w;
        end loop;
        close C01;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
