CREATE OR REPLACE FUNCTION obter_se_exibe_unidade ( nr_seq_interno_p bigint, dt_prevista_p timestamp, nr_seq_vaga_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
cd_unidade_compl_w	varchar(10);
cd_unidade_basica_w	varchar(10);
nr_seq_interno_w	bigint;
cd_setor_atendimento_w	integer;
dt_saida_w		timestamp;
ie_exibe_leito_w	varchar(1):='N';


BEGIN 
 
 
	select	max(cd_setor_atendimento), 
		max(cd_unidade_basica), 
		max(cd_unidade_compl) 
	into STRICT	cd_setor_atendimento_w, 
		cd_unidade_basica_w, 
		cd_unidade_compl_w 
	from	unidade_atendimento 
	where	nr_seq_interno = nr_seq_interno_p;
 
	 
	select	max(obter_dt_saida_leito(nr_sequencia,cd_setor_desejado,coalesce(obter_data_entrada(nr_atendimento),dt_prevista),qt_dia,nr_seq_interno_p)) 
	into STRICT	dt_saida_w 
	from	gestao_vaga 
	where	cd_setor_desejado	= cd_setor_atendimento_w 
	and	cd_unidade_basica	= cd_unidade_basica_w 
	and	cd_unidade_compl	= cd_unidade_compl_w 
	and	nr_sequencia		<> nr_seq_vaga_p;
	 
	if (coalesce(dt_saida_w::text, '') = '') then 
		ie_exibe_leito_w := 'S';
	elsif (dt_saida_w IS NOT NULL AND dt_saida_w::text <> '') and (trunc(dt_prevista_p) >= trunc(dt_saida_w)) then 
		ie_exibe_leito_w := 'S';
	end if;	
 
return	ie_exibe_leito_w;
	 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
