CREATE OR REPLACE FUNCTION obter_se_exibir_ivc ( nr_atendimento_p bigint, ie_status_ivc_p text, nr_prescricao_p bigint, qt_hora_prescricao_p bigint, dt_conulta_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_exibir_ivc_w	varchar(1) := 'S';
ie_ivc_atend_w	varchar(1);
ie_ivc_aberto_w	varchar(1);
ie_rep_valida_w	varchar(1);


BEGIN
if (ie_status_ivc_p IS NOT NULL AND ie_status_ivc_p::text <> '') and (ie_status_ivc_p in ('I','F')) then
	begin
	ie_exibir_ivc_w	:= 'S';
	end;
elsif (ie_status_ivc_p = 'P') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_ivc_atend_w
	from	adep_irrigacao
	where	nr_atendimento	= nr_atendimento_p;

	if (ie_ivc_atend_w = 'S') then
		begin
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_ivc_aberto_w
		from	adep_irrigacao
		where	nr_atendimento	= nr_atendimento_p
		and	ie_status_ivc	= 'I';

		if (ie_ivc_aberto_w = 'N') then
			begin
			select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_rep_valida_w
			from	prescr_medica
			where	nr_prescricao		= nr_prescricao_p
			and	(((coalesce(dt_conulta_p::text, '') = '') and (dt_validade_prescr	> clock_timestamp() - qt_hora_prescricao_p)) or (dt_validade_prescr between dt_conulta_p and (dt_conulta_p + qt_hora_prescricao_p/24)) or (dt_inicio_prescr between dt_conulta_p and (dt_conulta_p + qt_hora_prescricao_p/24)));

			ie_exibir_ivc_w	:= ie_rep_valida_w;
			end;
		else
			begin
			ie_exibir_ivc_w	:= 'N';
			end;
		end if;
		end;
	else
		begin
		ie_exibir_ivc_w	:= 'S';
		end;
	end if;
	end;
elsif (ie_status_ivc_p = 'X') then
	begin
	ie_exibir_ivc_w	:= 'N';
	end;
end if;

return ie_exibir_ivc_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
