CREATE OR REPLACE FUNCTION obter_se_exib_proced_patologia (nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_retorno_w	varchar(1)	:= 'N';
ap_w			varchar(1)	:= 'N';
aph_w			varchar(1)	:= 'N';
apc_w			varchar(1)	:= 'N';


BEGIN
if (nr_prescricao_p > 0) and (nr_sequencia_p > 0) then
	select	Obter_se_exibe_proced(nr_prescricao,nr_sequencia,ie_tipo_proced, 'AP'),
			Obter_se_exibe_proced(nr_prescricao,nr_sequencia,ie_tipo_proced,'APH'),
			Obter_se_exibe_proced(nr_prescricao,nr_sequencia,ie_tipo_proced, 'APC')
	into STRICT	ap_w,
			aph_w,
			apc_w
	from	prescr_procedimento a
	where	a.nr_prescricao	= nr_prescricao_p
	and		a.nr_sequencia	= nr_sequencia_p;

	if (ap_w = 'S') or (aph_w = 'S') or (apc_w = 'S') then
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
