SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_exige_aval_inspecao (nr_seq_registro_p bigint) RETURNS varchar AS $body$
DECLARE



cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
ie_exige_aval_inspecao_w		varchar(1) := 'N';
ie_exige_avaliacao_w			varchar(1);
cd_material_w				integer;
cd_cgc_w				inspecao_recebimento.cd_cgc%type;
qt_registros_w				bigint;
nr_sequencia_w				exige_inspecao_item_nf.nr_sequencia%type;

c01 CURSOR FOR
SELECT	cd_material,
	cd_cgc
from	inspecao_recebimento
where	nr_seq_registro = nr_seq_registro_p
order by cd_material;

c02 CURSOR FOR
SELECT	coalesce(ie_exige_avaliacao,'N'),
	nr_sequencia
from	exige_inspecao_item_nf
where	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_w) 			= cd_material_w or cd_material_w = 0)
order by coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

open c01;
loop
fetch c01 into
	cd_material_w,
	cd_cgc_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	a.cd_grupo_material,
		a.cd_subgrupo_material,
		a.cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v a
	where	a.cd_material = cd_material_w;

	open c02;
	loop
	fetch c02 into
		ie_exige_avaliacao_w,
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		nr_sequencia_w := nr_sequencia_w;
		if (ie_exige_aval_inspecao_w = 'N') then
			ie_exige_aval_inspecao_w := ie_exige_avaliacao_w;
		end if;
		end;
	end loop;
	close c02;


	select	count(*)
	into STRICT	qt_registros_w
	from	exige_inspecao_item_nf_for
	where	nr_seq_regra = nr_sequencia_w;

	if (qt_registros_w > 0) then

		select	count(*)
		into STRICT	qt_registros_w
		from	exige_inspecao_item_nf_for
		where	nr_seq_regra = nr_sequencia_w
		and	cd_cnpj = cd_cgc_w;

		if (qt_registros_w = 0) then
			ie_exige_aval_inspecao_w := 'N';
		end if;
	end if;
	end;
end loop;
close c01;

return coalesce(ie_exige_aval_inspecao_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
