CREATE OR REPLACE FUNCTION obter_se_exige_cpf_js (cd_pessoa_fisica_p text, cd_pessoa_responsavel_p text, ie_exige_cpf_conv_p text) RETURNS varchar AS $body$
DECLARE


ie_exige_cpf_w		varchar(1) := 'N';	
qt_idade_pf_w		bigint;	
cd_pessoa_fisica_w      pessoa_fisica.cd_pessoa_fisica%type;
nr_cpf_w		pessoa_fisica.nr_cpf%type;
ie_brasileiro_w		varchar(1);
			

BEGIN

-- ie_exige_cpf_conv_p param 501 da EUP
cd_pessoa_fisica_w := cd_pessoa_fisica_p;

if (ie_exige_cpf_conv_p = 'S') then

	qt_idade_pf_w:= Obter_Idade_PF(cd_pessoa_fisica_p, clock_timestamp(), 'A');
	if (coalesce(qt_idade_pf_w,0) < 18) and (cd_pessoa_responsavel_p IS NOT NULL AND cd_pessoa_responsavel_p::text <> '') then
		cd_pessoa_fisica_w := cd_pessoa_responsavel_p;
	end if;
	
	select	max(trim(both coalesce(nr_cpf,obter_compl_pf(cd_pessoa_fisica,3,'C'))))
	into STRICT	nr_cpf_w
        from   	pessoa_fisica
        where 	cd_pessoa_fisica = cd_pessoa_fisica_w;
	
	ie_brasileiro_w := obter_se_brasileiro(cd_pessoa_fisica_w);
	
	if (coalesce(nr_cpf_w::text, '') = '') and (ie_brasileiro_w = 'S') then
		ie_exige_cpf_w := 'S';
	end if;
	
end if;

return	ie_exige_cpf_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
